INSERT INTO `page` VALUES (149,'CSS - HTML',NULL,NULL,'htm',NULL,NULL,'b {\r\n  color: #000000;\r\n}\r\n\r\nol, ul {\r\n  margin: 0;\r\n  margin-left: 2em;\r\n  padding: 0;\r\n}\r\n\r\nol {\r\n  color: #000000;\r\n}\r\n\r\nol span {\r\n  color: #000000;\r\n}\r\n\r\nul {\r\n  list-style-image: url();\r\n}\r\n\r\ndel {\r\n  color: #000000;\r\n}\r\n\r\ncode {\r\n  margin: 10px 15px;\r\n  padding: 10px;\r\n  display: block;\r\n  overflow: auto;\r\n  \r\n  font: 400 1em courier, monospace;\r\n  line-height: 120%;\r\n  white-space: pre;\r\n  \r\n  background: #ffffff;\r\n}\r\n\r\n.leftColumn code {\r\n  margin-left: 0;\r\n}\r\n\r\nacronym {\r\n  cursor: help;\r\n  border-bottom: 1px solid #777;\r\n}\r\n\r\ndt {\r\n  font-weight: bold;\r\n  color: #444444;\r\n}\r\n\r\ndd {\r\n  margin-left: 0;\r\n  padding-left: 45px; \r\n}\r\n\r\n\r\n\r\n\r\n/**************************************************************\r\n   Form Elements\r\n **************************************************************/\r\n\r\nlabel {\r\n  display: block;\r\n}\r\n\r\ninput,\r\ntextarea,\r\nselect {\r\n  padding: 2px;\r\n  font: 400 1em verdana, sans-serif;\r\n  color: #000;\r\n  background: #dddddd;\r\n  border: 1px solid #000;\r\n}\r\n\r\ninput:focus,\r\ninput:hover,\r\ntextarea:focus,\r\ntextarea:hover,\r\nselect:focus,\r\nselect:hover {\r\n  color: #000;\r\n  background: #dddddd;\r\n  border: 1px solid #444;\r\n}\r\n\r\n\r\ninput.button {\r\n  padding: 2px 5px;\r\n  font: 200 0.8em \"trebuchet ms\", serif;\r\n  color: #000000;\r\n  background: #dddddd;\r\n  border-width: 2px;\r\n  border-style: solid;\r\n  border-color: #888 #444 #444 #888;\r\n}\r\n\r\n\r\n\r\n\r\n\r\n\r\n/**************************************************************\r\n   Blockquote and included icons\r\n **************************************************************/\r\n\r\nblockquote {\r\n  margin: 10px 15px;\r\n  padding-left: 27px;\r\n  \r\n  background-color: #96BDE2; \r\n  background-image: url();\r\n  background-repeat: no-repeat;\r\n  \r\n}\r\n\r\nblockquote.exclamation {\r\n  background-image: url();\r\n}\r\n\r\nblockquote.stop {\r\n  background-image: url();\r\n}\r\n\r\nblockquote.go {\r\n  background-image: url();\r\n}\r\n\r\n\r\n\r\n\r\n\r\n/**************************************************************\r\n   Images\r\n **************************************************************/\r\n\r\nimg.floatRight {\r\n  margin: 5px 0px 10px 15px;  \r\n}\r\n\r\nimg.floatLeft {\r\n  margin: 5px 15px 10px 0px;\r\n}\r\n\r\na img {  \r\n/*  border: 2px solid #8BE6FA;           */\r\n}\r\n\r\na:hover img {  \r\n  /* Fixes IE bug - IE doesn&#39;t correctly apply the style on a:hover so need to mask it */\r\n/*  border: 2px solid #FFF !important; */\r\n/*  border: 2px solid #8BE6FA;           */\r\n}\r\n\r\n\r\n/**************************************************************\r\n   Admin Elements\r\n **************************************************************/\r\n\r\n.adminpanel {\r\n  margin-left:15px; \r\n  margin-right: 20px;\r\n  padding: 1em;\r\n\r\n\r\n  border: 1px solid #444;\r\n  color: #444;\r\n  background: #eee;\r\n  vertical-align: middle;\r\n  padding: 10;\r\n\r\n}\r\n\r\np.notice {\r\n	font: italic 10pt/14pt Georgia,serif;\r\n	margin: 15px;\r\n        padding: 10px;\r\n        text-align: center;\r\n	border: 1px solid #EA1B00;\r\n        color: #000000;\r\n}\r\n\r\np.login {\r\n	font: 10pt/14pt verdana, arial, sans-serif; \r\n        font-variant: small-caps;\r\n	margin: 0px;\r\n        margin-right:15px;\r\n        padding: 0px;\r\n        text-align: right;\r\n        color: #000000;\r\n}\r\n\r\np.nav {\r\n	font: 10pt/14pt verdana, arial, sans-serif;\r\n        font-variant: small-caps;\r\n	margin: 0px;\r\n        margin-right:15px;\r\n        padding: 0px;\r\n        text-align: right;\r\n        color: #000000;\r\n}\r\n','Friday','b {\r\n  color: #000000;\r\n}\r\n\r\nol, ul {\r\n  margin: 0;\r\n  margin-left: 2em;\r\n  padding: 0;\r\n}\r\n\r\nol {\r\n  color: #000000;\r\n}\r\n\r\nol span {\r\n  color: #000000;\r\n}\r\n\r\nul {\r\n  list-style-image: url();\r\n}\r\n\r\ndel {\r\n  color: #000000;\r\n}\r\n\r\ncode {\r\n  margin: 10px 15px;\r\n  padding: 10px;\r\n  display: block;\r\n  overflow: auto;\r\n  \r\n  font: 400 1em courier, monospace;\r\n  line-height: 120%;\r\n  white-space: pre;\r\n  \r\n  background: #ffffff;\r\n}\r\n\r\n.leftColumn code {\r\n  margin-left: 0;\r\n}\r\n\r\nacronym {\r\n  cursor: help;\r\n  border-bottom: 1px solid #777;\r\n}\r\n\r\ndt {\r\n  font-weight: bold;\r\n  color: #444444;\r\n}\r\n\r\ndd {\r\n  margin-left: 0;\r\n  padding-left: 45px; \r\n}\r\n\r\n\r\n\r\n\r\n/**************************************************************\r\n   Form Elements\r\n **************************************************************/\r\n\r\nlabel {\r\n  display: block;\r\n}\r\n\r\ninput,\r\ntextarea,\r\nselect {\r\n  padding: 2px;\r\n  font: 400 1em verdana, sans-serif;\r\n  color: #000;\r\n  background: #dddddd;\r\n  border: 1px solid #000;\r\n}\r\n\r\ninput:focus,\r\ninput:hover,\r\ntextarea:focus,\r\ntextarea:hover,\r\nselect:focus,\r\nselect:hover {\r\n  color: #000;\r\n  background: #dddddd;\r\n  border: 1px solid #444;\r\n}\r\n\r\n\r\ninput.button {\r\n  padding: 2px 5px;\r\n  font: 200 0.8em \"trebuchet ms\", serif;\r\n  color: #000000;\r\n  background: #dddddd;\r\n  border-width: 2px;\r\n  border-style: solid;\r\n  border-color: #888 #444 #444 #888;\r\n}\r\n\r\n\r\n\r\n\r\n\r\n\r\n/**************************************************************\r\n   Blockquote and included icons\r\n **************************************************************/\r\n\r\nblockquote {\r\n  margin: 10px 15px;\r\n  padding-left: 27px;\r\n  \r\n  background-color: #96BDE2; \r\n  background-image: url();\r\n  background-repeat: no-repeat;\r\n  \r\n}\r\n\r\nblockquote.exclamation {\r\n  background-image: url();\r\n}\r\n\r\nblockquote.stop {\r\n  background-image: url();\r\n}\r\n\r\nblockquote.go {\r\n  background-image: url();\r\n}\r\n\r\n\r\n\r\n\r\n\r\n/**************************************************************\r\n   Images\r\n **************************************************************/\r\n\r\nimg.floatRight {\r\n  margin: 5px 0px 10px 15px;  \r\n}\r\n\r\nimg.floatLeft {\r\n  margin: 5px 15px 10px 0px;\r\n}\r\n\r\na img {  \r\n/*  border: 2px solid #8BE6FA;           */\r\n}\r\n\r\na:hover img {  \r\n  /* Fixes IE bug - IE doesn\'t correctly apply the style on a:hover so need to mask it */\r\n/*  border: 2px solid #FFF !important; */\r\n/*  border: 2px solid #8BE6FA;           */\r\n}\r\n\r\n\r\n/**************************************************************\r\n   Admin Elements\r\n **************************************************************/\r\n\r\n.adminpanel {\r\n  margin-left:15px; \r\n  margin-right: 20px;\r\n  padding: 1em;\r\n\r\n\r\n  border: 1px solid #444;\r\n  color: #444;\r\n  background: #eee;\r\n  vertical-align: middle;\r\n  padding: 10;\r\n\r\n}\r\n\r\np.notice {\r\n	font: italic 10pt/14pt Georgia,serif;\r\n	margin: 15px;\r\n        padding: 10px;\r\n        text-align: center;\r\n	border: 1px solid #EA1B00;\r\n        color: #000000;\r\n}\r\n\r\np.login {\r\n	font: 10pt/14pt verdana, arial, sans-serif; \r\n        font-variant: small-caps;\r\n	margin: 0px;\r\n        margin-right:15px;\r\n        padding: 0px;\r\n        text-align: right;\r\n        color: #000000;\r\n}\r\n\r\np.nav {\r\n	font: 10pt/14pt verdana, arial, sans-serif;\r\n        font-variant: small-caps;\r\n	margin: 0px;\r\n        margin-right:15px;\r\n        padding: 0px;\r\n        text-align: right;\r\n        color: #000000;\r\n}\r\n',NULL,'CSS',NULL,NULL,'no','no','no','1',1292786688,0,NULL,NULL,NULL,NULL,7,NULL,NULL,'css/html.css',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),(150,'CSS - Layout',NULL,NULL,'htm',NULL,NULL,'/**************************************************************\r\n   gRSShopper HTML CSS\r\n   Stephen Downes, June 2012\r\n   DATE:    2006.03.19\r\n   PURPOSE: Controls the layout of the site and styles\r\n            the menus\r\n **************************************************************/\r\n\r\n\r\n/**************************************************************\r\n   #content: Sets the width of the site.  Make changes here\r\n             to convert to a fixed width site.\r\n **************************************************************/\r\n\r\n#content {  \r\n  height: 100%;\r\n  min-height: 100%;\r\n\r\n  text-align: left;\r\n}\r\n\r\n#content,\r\n#width {\r\n  /* max-width hack for IE since it doesn&#39;t understand the valid css property */\r\n  width: expression(document.body.clientWidth > 1000 ? \"1000px\" : \"94%\");  \r\n  max-width: 1000px;\r\n  margin: 0 auto;\r\n}\r\n\r\n#content[id],\r\n#width[id] {\r\n  width: 94%;\r\n  height: auto;\r\n}\r\n\r\n\r\n\r\n\r\n/**************************************************************\r\n   #header: Holds the site title and header images\r\n **************************************************************/\r\n\r\n#header {\r\n  position: relative;\r\n  height: 100px;\r\n}\r\n\r\n/* Holds the site title and subtitle */\r\n#header #title {\r\n  position: absolute;\r\n  z-index: 3;\r\n  top: 10px;\r\n  left: 130px;\r\n\r\n  padding: 5px;\r\n  text-align: right;\r\n}\r\n\r\n#header h1 {\r\n  margin: 0;\r\n  padding: 0;\r\n\r\n  font: 700 4em \"trebuchet ms\", serif;\r\n  letter-spacing: -3px;\r\n  text-transform: lowercase;\r\n  color: #000000;\r\n}\r\n\r\n#header h2 {\r\n  position: absolute;\r\n  top: 10px;\r\n  right: 5px;\r\n\r\n  margin: 0;\r\n  padding: 0;\r\n\r\n  font: 700 1em \"trebuchet ms\", serif;\r\n  text-transform: lowercase;\r\n  color: #000000;\r\n}\r\n\r\n/* Floating Images */\r\n\r\n\r\nimg.left {\r\n  float:left;\r\n  margin:5px 15px 15px 15px; \r\n}\r\n\r\n\r\nimg.right {\r\n  float:right;\r\n  margin:5px 15px 15px 15px; \r\n}\r\n\r\nimg.top {\r\n  margin:5px 15px 15px 0px; \r\n}\r\n\r\nimg.bottom {\r\n  margin:15px 15px 5px 0px; \r\n}\r\n\r\n/* Sets where the header images will go */\r\n#header img.left {\r\n  position: absolute;\r\n  z-index: 1;\r\nmargin:0;\r\n  top: 0;\r\n  left: 0;\r\n}\r\n\r\n#header img.right {\r\n  position: absolute;\r\n  z-index: 0;\r\nmargin:0;\r\n  top: 0;\r\n  right: 0;\r\n}\r\n\r\n#header img.balloons {\r\n  position: absolute;\r\n  z-index: 2;\r\n\r\n  top: 70px;\r\n  right: 400px;\r\n}\r\n\r\n\r\n\r\n/**************************************************************\r\n   #mainMenu: The top level site menu\r\n **************************************************************/\r\n\r\n#mainMenu { \r\n  float: left;\r\n  width: 100%;\r\n  clear: both;  \r\n}\r\n\r\n#mainMenu ul {\r\n  margin: 0;\r\n  padding: 0;\r\n}\r\n\r\n#mainMenu li {\r\n  display: inline;\r\n  list-style: none;\r\n  margin: 0;\r\n  padding: 0;\r\n}\r\n\r\n#mainMenu li a {\r\n  float: left;\r\n  margin: 0 2px;\r\n  padding: 5px 0.5em;\r\n\r\n  font: 400 1.6em \"trebuchet ms\", serif;\r\n  text-decoration: none;\r\n  text-transform: lowercase;\r\n  color: #000000;\r\n}\r\n\r\n#mainMenu li a:hover,\r\n#mainMenu li a.here {\r\n  color: #000000;\r\n  border-top: 2px solid #888888;\r\n}\r\n\r\n#mainMenu li a.last {\r\n  margin-right: 0;\r\n}\r\n\r\n\r\n\r\n\r\n/**************************************************************\r\n   .sideMenu: The side bar menu\r\n **************************************************************/\r\n\r\nul.sideMenu {\r\n  margin: 0;\r\n  padding: 0;\r\n}\r\n\r\n.sideMenu li {\r\n  display: inline;\r\n  \r\n  /* Needed since IE fails when you give it list-style: none; */  \r\n  list-style-image: url(foo.gif);\r\n  font: 400 1.3em \"trebuchet ms\", serif;\r\n}\r\n\r\n.sideMenu li a {\r\n  display: block;\r\n  margin: 0.2em 0;\r\n  padding: 3px 5px;    \r\n  \r\n  text-decoration: none;\r\n  color: #0000aa;\r\n} \r\n\r\n.sideMenu li a:hover {\r\n  color: #3578FF;\r\n  background: #0000ff;\r\n}\r\n\r\n/* Active menu item */\r\n.sideMenu li.here {\r\n  display: block;  \r\n  padding: 5px;\r\n  color: #000000;\r\n  background: #ffffff;\r\n}\r\n\r\n/* Submenu of active menu item */\r\n.sideMenu li.here ul {\r\n  margin: 0;\r\n  padding: 0;\r\n}\r\n\r\n.sideMenu li.here ul li a {\r\n  padding-left: 35px;\r\n  font: 400 0.55em verdana, arial, sans-serif; \r\n  color: #000;  \r\n  background: url() no-repeat 10px 0px;\r\n}\r\n\r\n.sideMenu li.here ul li a:hover {\r\n  color: #0000ff;\r\n  background: #ffffff url() no-repeat 10px 0px;\r\n}\r\n\r\n\r\n\r\n/**************************************************************\r\n   #page: Holds the main page content.\r\n **************************************************************/\r\n\r\n#page {\r\n  float: left;\r\n  width: 100%;\r\n  clear: both;\r\n  \r\n  padding-bottom: 4em;\r\n}\r\n\r\n\r\n\r\n\r\n/**************************************************************\r\n   #footer: The page footer - will stick to the bottom if not\r\n            enough content.\r\n **************************************************************/\r\n\r\n#footer {\r\n  float: left;\r\n  width: 100%;\r\n  clear: both;\r\n  \r\n  margin-top: -3.8em;\r\n  background: #fff url() repeat-x top left;\r\n}\r\n\r\n/* Sets the width of the footer content */\r\n#footer #width {\r\n  position: relative;\r\n  z-index: 3;\r\n  font-size: 0.85em;\r\n  padding-top: 27px;\r\n}\r\n\r\n\r\n\r\n/**************************************************************\r\n   Width classes used by the site columns\r\n **************************************************************/\r\n\r\n.width100 {\r\n  width: 100%;\r\n}\r\n\r\n.width75 {\r\n  width: 74%;\r\n}\r\n\r\n.width50 {\r\n  width: 49.7%;\r\n}\r\n\r\n.width33 {\r\n  width: 32.7%;\r\n}\r\n\r\n.width25 {\r\n  width: 24.7%;\r\n}\r\n\r\n\r\n\r\n/**************************************************************\r\n   Alignment classes\r\n **************************************************************/\r\n\r\n.floatLeft {\r\n  float: left;\r\n}\r\n\r\n.floatRight {\r\n  float: right;\r\n}\r\n\r\n.alignLeft {\r\n  text-align: left;\r\n}\r\n\r\n.alignRight {\r\n  text-align: right;\r\n}\r\n\r\n\r\n\r\n/**************************************************************\r\n   Generic display classes\r\n **************************************************************/\r\n\r\n.clear {\r\n  clear: both;\r\n}\r\n\r\n.block {\r\n  display: block;\r\n}\r\n\r\n.small {\r\n  font-size: 0.8em;\r\n}\r\n\r\n.green {\r\n  color: #A1FF45;\r\n}\r\n\r\n.red {\r\n  color: #EA1B00;\r\n}\r\n\r\n.grey {\r\n  color: #666;\r\n}\r\n\r\n.grey a {\r\n  color: #999;\r\n}\r\n\r\n.grey a:hover {\r\n  color: #EEE;\r\n}\r\n\r\n.gradient {\r\n  margin-bottom: 2em;\r\n  padding: 1em;\r\n  border: 1px solid #000000;\r\n  background: #ffffff url() repeat-x top left; \r\n}\r\n\r\n\r\n\r\n.gradient2 {\r\n  margin-bottom: 2em;\r\n  border-top: 2px solid #000000;\r\n  border-right: 2px solid #000000;\r\n  border-bottom: 2px solid #000000;\r\n  background: #DCE3E8 url() repeat-x top left; \r\n}\r\n\r\n\r\n/********************\r\nInsets\r\n*********************/\r\n\r\n.leftinset {\r\n	padding:20px; \r\n	padding-top: 4px; \r\n	float:left;\r\n	margin-left:20px;\r\n\r\n}\r\n\r\n\r\n.rightinset {\r\n	padding: 20px;\r\n	float:right;\r\n	margin-right:20px;\r\n}\r\n\r\n\r\n.leftcallout {\r\n	font: italic 16pt/20pt Georgia,serif;\r\n	border-top: 2px solid #996600;\r\n	border-bottom: 2px solid #996600;\r\n	padding:20px; \r\n	float:left;\r\n	margin-left:-20px;\r\n	margin-right: 20px;\r\n	width: 250px;\r\n}\r\n\r\n\r\n.rightcallout {\r\n	font: italic 16pt/20pt Georgia,serif;\r\n	border-top: 2px solid #996600;\r\n	border-bottom: 2px solid #996600;\r\n	padding:20px; \r\n	float:right;\r\n	margin-right:20px;\r\n	margin-left: 20px;\r\n	width: 350px;\r\n	margin-top: 5px;\r\n	margin-bottom: 10px;\r\n}\r\n\r\n\r\n.insetcaption {\r\n\r\n	font: italic 10pt/14pt Georgia,serif;\r\n	width: 300px;\r\n	margin-top: 10px;\r\n	border: 1px solid #996600;\r\n}\r\n\r\n\r\n','Friday','/**************************************************************\r\n   AUTHOR:  Pat Heard (fullahead.org)\r\n   DATE:    2006.03.19\r\n   PURPOSE: Controls the layout of the site and styles\r\n            the menus\r\n **************************************************************/\r\n\r\n\r\n\r\n/**************************************************************\r\n   #content: Sets the width of the site.  Make changes here\r\n             to convert to a fixed width site.\r\n **************************************************************/\r\n\r\n#content {  \r\n  height: 100%;\r\n  min-height: 100%;\r\n\r\n  text-align: left;\r\n}\r\n\r\n#content,\r\n#width {\r\n  /* max-width hack for IE since it doesn&#39;t understand the valid css property */\r\n  width: expression(document.body.clientWidth > 1000 ? \"1000px\" : \"94%\");  \r\n  max-width: 1000px;\r\n  margin: 0 auto;\r\n}\r\n\r\n#content[id],\r\n#width[id] {\r\n  width: 94%;\r\n  height: auto;\r\n}\r\n\r\n\r\n\r\n\r\n/**************************************************************\r\n   #header: Holds the site title and header images\r\n **************************************************************/\r\n\r\n#header {\r\n  position: relative;\r\n  height: 100px;\r\n}\r\n\r\n/* Holds the site title and subtitle */\r\n#header #title {\r\n  position: absolute;\r\n  z-index: 3;\r\n  top: 10px;\r\n  left: 130px;\r\n\r\n  padding: 5px;\r\n  text-align: right;\r\n}\r\n\r\n#header h1 {\r\n  margin: 0;\r\n  padding: 0;\r\n\r\n  font: 700 4em \"trebuchet ms\", serif;\r\n  letter-spacing: -3px;\r\n  text-transform: lowercase;\r\n  color: #000000;\r\n}\r\n\r\n#header h2 {\r\n  position: absolute;\r\n  top: 10px;\r\n  right: 5px;\r\n\r\n  margin: 0;\r\n  padding: 0;\r\n\r\n  font: 700 1em \"trebuchet ms\", serif;\r\n  text-transform: lowercase;\r\n  color: #000000;\r\n}\r\n\r\n/* Floating Images */\r\n\r\n\r\nimg.left {\r\n  float:left;\r\n  margin:5px 15px 15px 15px; \r\n}\r\n\r\n\r\nimg.right {\r\n  float:right;\r\n  margin:5px 15px 15px 15px; \r\n}\r\n\r\nimg.top {\r\n  margin:5px 15px 15px 0px; \r\n}\r\n\r\nimg.bottom {\r\n  margin:15px 15px 5px 0px; \r\n}\r\n\r\n/* Sets where the header images will go */\r\n#header img.left {\r\n  position: absolute;\r\n  z-index: 1;\r\nmargin:0;\r\n  top: 0;\r\n  left: 0;\r\n}\r\n\r\n#header img.right {\r\n  position: absolute;\r\n  z-index: 0;\r\nmargin:0;\r\n  top: 0;\r\n  right: 0;\r\n}\r\n\r\n#header img.balloons {\r\n  position: absolute;\r\n  z-index: 2;\r\n\r\n  top: 70px;\r\n  right: 400px;\r\n}\r\n\r\n\r\n\r\n/**************************************************************\r\n   #mainMenu: The top level site menu\r\n **************************************************************/\r\n\r\n#mainMenu { \r\n  float: left;\r\n  width: 100%;\r\n  clear: both;  \r\n}\r\n\r\n#mainMenu ul {\r\n  margin: 0;\r\n  padding: 0;\r\n}\r\n\r\n#mainMenu li {\r\n  display: inline;\r\n  list-style: none;\r\n  margin: 0;\r\n  padding: 0;\r\n}\r\n\r\n#mainMenu li a {\r\n  float: left;\r\n  margin: 0 2px;\r\n  padding: 5px 0.5em;\r\n\r\n  font: 400 1.6em \"trebuchet ms\", serif;\r\n  text-decoration: none;\r\n  text-transform: lowercase;\r\n  color: #000000;\r\n}\r\n\r\n#mainMenu li a:hover,\r\n#mainMenu li a.here {\r\n  color: #000000;\r\n  border-top: 2px solid #888888;\r\n}\r\n\r\n#mainMenu li a.last {\r\n  margin-right: 0;\r\n}\r\n\r\n\r\n\r\n\r\n/**************************************************************\r\n   .sideMenu: The side bar menu\r\n **************************************************************/\r\n\r\nul.sideMenu {\r\n  margin: 0;\r\n  padding: 0;\r\n}\r\n\r\n.sideMenu li {\r\n  display: inline;\r\n  \r\n  /* Needed since IE fails when you give it list-style: none; */  \r\n  list-style-image: url(foo.gif);\r\n  font: 400 1.3em \"trebuchet ms\", serif;\r\n}\r\n\r\n.sideMenu li a {\r\n  display: block;\r\n  margin: 0.2em 0;\r\n  padding: 3px 5px;    \r\n  \r\n  text-decoration: none;\r\n  color: #0000aa;\r\n} \r\n\r\n.sideMenu li a:hover {\r\n  color: #3578FF;\r\n  background: #0000ff;\r\n}\r\n\r\n/* Active menu item */\r\n.sideMenu li.here {\r\n  display: block;  \r\n  padding: 5px;\r\n  color: #000000;\r\n  background: #ffffff;\r\n}\r\n\r\n/* Submenu of active menu item */\r\n.sideMenu li.here ul {\r\n  margin: 0;\r\n  padding: 0;\r\n}\r\n\r\n.sideMenu li.here ul li a {\r\n  padding-left: 35px;\r\n  font: 400 0.55em verdana, arial, sans-serif; \r\n  color: #000;  \r\n  background: url() no-repeat 10px 0px;\r\n}\r\n\r\n.sideMenu li.here ul li a:hover {\r\n  color: #0000ff;\r\n  background: #ffffff url() no-repeat 10px 0px;\r\n}\r\n\r\n\r\n\r\n/**************************************************************\r\n   #page: Holds the main page content.\r\n **************************************************************/\r\n\r\n#page {\r\n  float: left;\r\n  width: 100%;\r\n  clear: both;\r\n  \r\n  padding-bottom: 4em;\r\n}\r\n\r\n\r\n\r\n\r\n/**************************************************************\r\n   #footer: The page footer - will stick to the bottom if not\r\n            enough content.\r\n **************************************************************/\r\n\r\n#footer {\r\n  float: left;\r\n  width: 100%;\r\n  clear: both;\r\n  \r\n  margin-top: -3.8em;\r\n  background: #fff url() repeat-x top left;\r\n}\r\n\r\n/* Sets the width of the footer content */\r\n#footer #width {\r\n  position: relative;\r\n  z-index: 3;\r\n  font-size: 0.85em;\r\n  padding-top: 27px;\r\n}\r\n\r\n\r\n\r\n/**************************************************************\r\n   Width classes used by the site columns\r\n **************************************************************/\r\n\r\n.width100 {\r\n  width: 100%;\r\n}\r\n\r\n.width75 {\r\n  width: 74%;\r\n}\r\n\r\n.width50 {\r\n  width: 49.7%;\r\n}\r\n\r\n.width33 {\r\n  width: 32.7%;\r\n}\r\n\r\n.width25 {\r\n  width: 24.7%;\r\n}\r\n\r\n\r\n\r\n/**************************************************************\r\n   Alignment classes\r\n **************************************************************/\r\n\r\n.floatLeft {\r\n  float: left;\r\n}\r\n\r\n.floatRight {\r\n  float: right;\r\n}\r\n\r\n.alignLeft {\r\n  text-align: left;\r\n}\r\n\r\n.alignRight {\r\n  text-align: right;\r\n}\r\n\r\n\r\n\r\n/**************************************************************\r\n   Generic display classes\r\n **************************************************************/\r\n\r\n.clear {\r\n  clear: both;\r\n}\r\n\r\n.block {\r\n  display: block;\r\n}\r\n\r\n.small {\r\n  font-size: 0.8em;\r\n}\r\n\r\n.green {\r\n  color: #A1FF45;\r\n}\r\n\r\n.red {\r\n  color: #EA1B00;\r\n}\r\n\r\n.grey {\r\n  color: #666;\r\n}\r\n\r\n.grey a {\r\n  color: #999;\r\n}\r\n\r\n.grey a:hover {\r\n  color: #EEE;\r\n}\r\n\r\n.gradient {\r\n  margin-bottom: 2em;\r\n  border: 1px solid #000000;\r\n  background: #ffffff url() repeat-x top left; \r\n}\r\n\r\n\r\n\r\n.gradient2 {\r\n  margin-bottom: 2em;\r\n  border-top: 2px solid #000000;\r\n  border-right: 2px solid #000000;\r\n  border-bottom: 2px solid #000000;\r\n  background: #DCE3E8 url() repeat-x top left; \r\n}\r\n\r\n\r\n/********************\r\nInsets\r\n*********************/\r\n\r\n.leftinset {\r\n	padding:20px; \r\n	padding-top: 4px; \r\n	float:left;\r\n	margin-left:20px;\r\n\r\n}\r\n\r\n\r\n.rightinset {\r\n	padding: 20px;\r\n	float:right;\r\n	margin-right:20px;\r\n}\r\n\r\n\r\n.leftcallout {\r\n	font: italic 16pt/20pt Georgia,serif;\r\n	border-top: 2px solid #996600;\r\n	border-bottom: 2px solid #996600;\r\n	padding:20px; \r\n	float:left;\r\n	margin-left:-20px;\r\n	margin-right: 20px;\r\n	width: 250px;\r\n}\r\n\r\n\r\n.rightcallout {\r\n	font: italic 16pt/20pt Georgia,serif;\r\n	border-top: 2px solid #996600;\r\n	border-bottom: 2px solid #996600;\r\n	padding:20px; \r\n	float:right;\r\n	margin-right:20px;\r\n	margin-left: 20px;\r\n	width: 350px;\r\n	margin-top: 5px;\r\n	margin-bottom: 10px;\r\n}\r\n\r\n\r\n.insetcaption {\r\n\r\n	font: italic 10pt/14pt Georgia,serif;\r\n	width: 300px;\r\n	margin-top: 10px;\r\n	border: 1px solid #996600;\r\n}\r\n\r\n',NULL,'CSS',NULL,NULL,'no','no','no','1',1292786761,0,NULL,NULL,NULL,NULL,7,NULL,NULL,'css/layout.css',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),(151,'CSS - Print',NULL,NULL,'htm',NULL,NULL,'/**************************************************************\r\n   AUTHOR:  Pat Heard (fullahead.org)\r\n   DATE:    2006.03.19\r\n   PURPOSE: Styles the page for print.  Techniques adopted from\r\n            http://www.alistapart.com/articles/goingtoprint/\r\n **************************************************************/\r\n\r\nbody {\r\n  font: 400 12pt verdana, arial, sans-serif;\r\n  color: #000;\r\n  background: #FFF;\r\n}\r\n\r\nh1 {\r\n  margin-top: 30px;\r\n  font-size: 18pt;\r\n  border-bottom: 1px solid #999;\r\n}\r\n\r\nh2 {\r\n  font-size: 16pt;\r\n}\r\n\r\nh3 {\r\n  font-size: 14pt;\r\n}\r\n\r\nh4, h5, h6 {\r\n  font-size: 12pt;\r\n}\r\n\r\nacronym {\r\n  border: 0;\r\n}\r\n\r\na {\r\n  color: #00F;\r\n}\r\n\r\n/* Prints the URL after a link in CSS2 supporting browsers */\r\na:link:after, \r\na:visited:after {\r\n   content: \" (\" attr(href) \") \";\r\n   font-size: 90%;\r\n}\r\n\r\n\r\n/* Don&#39;t display these elements since they&#39;re not useful on paper */\r\n#header,\r\n#mainMenu,\r\n.sideMenu,\r\n#footer {\r\n  display: none;\r\n}\r\n\r\n#content {\r\n  width: auto;\r\n  margin: 0 8%;\r\n  padding: 0;\r\n}\r\n\r\n\r\n','Friday','/**************************************************************\r\n   AUTHOR:  Pat Heard (fullahead.org)\r\n   DATE:    2006.03.19\r\n   PURPOSE: Styles the page for print.  Techniques adopted from\r\n            http://www.alistapart.com/articles/goingtoprint/\r\n **************************************************************/\r\n\r\nbody {\r\n  font: 400 12pt verdana, arial, sans-serif;\r\n  color: #000;\r\n  background: #FFF;\r\n}\r\n\r\nh1 {\r\n  margin-top: 30px;\r\n  font-size: 18pt;\r\n  border-bottom: 1px solid #999;\r\n}\r\n\r\nh2 {\r\n  font-size: 16pt;\r\n}\r\n\r\nh3 {\r\n  font-size: 14pt;\r\n}\r\n\r\nh4, h5, h6 {\r\n  font-size: 12pt;\r\n}\r\n\r\nacronym {\r\n  border: 0;\r\n}\r\n\r\na {\r\n  color: #00F;\r\n}\r\n\r\n/* Prints the URL after a link in CSS2 supporting browsers */\r\na:link:after, \r\na:visited:after {\r\n   content: \" (\" attr(href) \") \";\r\n   font-size: 90%;\r\n}\r\n\r\n\r\n/* Don\'t display these elements since they\'re not useful on paper */\r\n#header,\r\n#mainMenu,\r\n.sideMenu,\r\n#footer {\r\n  display: none;\r\n}\r\n\r\n#content {\r\n  width: auto;\r\n  margin: 0 8%;\r\n  padding: 0;\r\n}\r\n\r\n\r\n',NULL,'CSS',NULL,NULL,'no','no','no','1',1292786819,0,NULL,NULL,NULL,NULL,7,NULL,NULL,'css/print.css',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),(154,'Contact',NULL,NULL,'htm',NULL,NULL,'<h2>Contact Me</h2>\r\n\r\n<p>Contact: <a  href=\"mailto:stephen@downes.ca\">stephen@downes.ca</a></p>','Friday','<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Strict//EN\"\r\n    \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\" xml:lang=\"en\" lang=\"en-AU\">\r\n\r\n<!-- mooc.ca template page_header -->\r\n\r\n<head>\r\n\r\n<!-- title -->\r\n  <title>Contact ~ Connectivism &amp; Connective Knowledge 2012</title>\r\n\r\n<!-- scripts -->\r\n  <script type=\"text/javascript\" src=\"http://cck12.mooc.ca/js/downes.js\"></script>\r\n  <script type=\"text/javascript\" src=\"http://cck12.mooc.ca/js/jquery.js\"></script>\r\n  <script type=\"text/javascript\" src=\"http://cck12.mooc.ca/js/jquery.jdpicker.js\"></script>\r\n\r\n\r\n\r\n<!-- style -->\r\n  <link rel=\"stylesheet\" type=\"text/css\" href=\"http://cck12.mooc.ca/css/jdpicker.css\" media=\"screen, projection, tv \" />\r\n  <link rel=\"stylesheet\" type=\"text/css\" href=\"http://cck12.mooc.ca/css/html.css\" media=\"screen, projection, tv \" />\r\n  <link rel=\"stylesheet\" type=\"text/css\" href=\"http://cck12.mooc.ca/css/layout.css\" media=\"screen, projection, tv\" />\r\n  <link rel=\"stylesheet\" type=\"text/css\" href=\"http://cck12.mooc.ca/css/print.css\" media=\"print\" />\r\n\r\n\r\n<!-- links -->  \r\n  <link rel=\"icon\" href=\"http://cck12.mooc.ca/favicon.ico\" type=\"image/x-icon\"/>\r\n  <link rel=\"alternate\" type=\"application/rss+xml\" title=\"RSS\" href=\"http://cck12.mooc.ca/newsletter.xml\" />\r\n  <meta http-equiv=\"Content-Type\" content=\"application/xhtml+xml; charset=utf-8\" />\r\n\r\n<!-- meta -->\r\n  <meta name=\"author\" content=\"mooc.ca\" />\r\n  <meta name=\"keywords\" content=\"Connectivism and Connective Knowledge,Massive Open Online Course,MOOC,online learning,new media,e-learning,RSS,syndication,connectivism,learning,education\" />\r\n  <meta name=\"description\" content=\"cck11.MOOC.ca\" />\r\n\r\n\r\n</head>\r\n\r\n\r\n<body>\r\n\r\n\r\n\r\n<!-- CONTENT div: Holds all site content except for the footer.  This is what causes the footer to stick to the bottom -->\r\n<div id=\"content\">\r\n\r\n\r\n  <!-- HEADER: Holds title, subtitle and header images -->\r\n  <div id=\"header\">\r\n\r\n      <a  href=\"http://cck12.mooc.ca/\"><img src=\"http://cck12.mooc.ca/images/moocheader.gif\" alt=\"mooc.ca\" border=\"0\"/></a>\r\n      <hr>\r\n\r\n  </div>\r\n\r\n\r\n  <!-- MAIN MENU: Top horizontal menu of the site.  Use class=\"here\" to turn the current page tab on -->\r\n  <div id=\"mainMenu\">\r\n    <ul class=\"floatRight\">\r\n      <li><a  href=\"http://cck12.mooc.ca/index.html\" title=\"Introduction\">Home</a></li>\r\n      <li><a  href=\"http://cck12.mooc.ca/about.htm\" title=\"About\">About</a></li>\r\n      <li><a  href=\"http://cck12.mooc.ca/contact.htm\" title=\"Get in touch\" class=\"last\">Contact</a></li>\r\n    </ul>\r\n  </div>\r\n\r\n\r\n\r\n  <!-- PAGE CONTENT BEGINS: This is where you would define the columns (number, width and alignment) -->\r\n  <div id=\"page\">\r\n\r\n    <!-- Sidebar -->\r\n   <div class=\"width25 floatLeft leftColumn\">\r\n      <div class=\"gradient\">\r\n           <h2 style=\"margin-left:10px;\">Contents</h2>\r\n\r\n<p style=\"margin-left:10px;\">\r\n\r\n<b>This Course</b><br />\r\n\r\n<a  href=\"http://cck12.mooc.ca/index.html\"